mapper.select:
select product_id, product_name, product_desc, img_address, normal_price, promotion_price, priority, create_time, last_edit_time, enable_status, product_category_id, shop_id from product
and shop_id = #{productCondition.shop.shopId}
and product_category_id = #{productCondition.productCategory.productCategoryId}
and product_name like '%${productCondition.productName}%'
and enable_status = #{productCondition.enableStatus}
order by priority desc LIMIT #{rowIndex},#{pageSize}

select count(1) from product

    
        and shop_id = #{productCondition.shop.shopId}
    
    
        and product_category_id = #{productCondition.productCategory.productCategoryId}
    
    
    
        and product_name like '%${productCondition.productName}%'
    
    
        and enable_status = #{productCondition.enableStatus}

mapper.update:
update product
product_name=#{productName}, product_desc=#{productDesc}, img_address=#{imgAddress}, normal_price=#{normalPrice}, promotion_price=#{promotionPrice}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus},
product_category_id=#{productCategory.productCategoryId}
where product_id = #{productId} and shop_id = #{shop.shopId}
